loadWordLists :: SyntaxState s => String -> String -> IO (WordList s)
loadWordLists _ _ = return WordList {wCmavos = fmap TS.fromList selmahosLS
                                    ,wGismus = TS.fromList gismuLS
                                    ,wBai = handleBAI baiLS
                                    }

